function incrementValue(n){n.preventDefault();var t=$(n.target).data("field"),i=$(n.target).closest("div"),r=parseInt(i.find("input[name="+t+"]").val(),10);isNaN(r)?i.find("input[name="+t+"]").val(0):i.find("input[name="+t+"]").val(r+1)}function decrementValue(n){n.preventDefault();var t=$(n.target).data("field"),i=$(n.target).closest("div"),r=parseInt(i.find("input[name="+t+"]").val(),10);!isNaN(r)&&r>0?i.find("input[name="+t+"]").val(r-1):i.find("input[name="+t+"]").val(0)}$(".input-group").on("click",".button-plus",function(n){incrementValue(n)});$(".input-group").on("click",".button-minus",function(n){decrementValue(n)});